import React from 'react';
import { connect } from 'react-redux'
import { Popover, NavBar, Icon, Button } from 'antd-mobile';
import 'antd-mobile/dist/antd-mobile.css';
import { Grid } from 'antd-mobile';
import '../assets/css/style.css'
import get from '../utils/http'





class HotCate extends React.Component  {

    constructor(props){
        super(props)

        this.state = {
            hostlist:[]
        }
        console.log("hostlist:")
        console.log(this.state.hostlist)
    }


    async componentDidMount(){
        let result = await get("http://localhost:9000/hotcat");

        console.log("result:")
        console.log(result)

        let data = result.map((value)=>({
            icon:value.img,
            title:value.title
        }))

        this.setState({
            hostlist:data
        })



    }

    render() {


        if(this.props.state.hostlist.length>0){
            return (

                <div>
                    <div className="sub-title">Custom content</div>
                    <Grid data={this.props.state.hostlist}
                        hasLine={false}
                        columnNum={3}
                        renderItem={dataItem => (
                            <div style={{ padding: '12.5px' }}>
                                <img src={dataItem.icon} style={{ width: '75px', height: '75px' }} alt="" />
                                <div style={{ color: '#888', fontSize: '14px', marginTop: '12px' }}>
                                    <span>热门分类</span>
                                </div>
                            </div>
                        )}
                    />

                </div>

            )
        }


    }
}

