import React from 'react';

class HotList extends React.Component {
    state = {
        value: '美食',
    };
    componentDidMount() {
        this.autoFocusInst.focus();
    }
    onChange = (value) => {
        this.setState({ value });
    };
    clear = () => {
        this.setState({ value: '' });
