import React from 'react';
import { connect } from 'react-redux'
import { Popover, NavBar, Icon, Button } from 'antd-mobile';
import 'antd-mobile/dist/antd-mobile.css';
import fns from "../store/asynMethods"

class DatiCounter extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
            currentTimu: 0,
            timuArray: []
        }
    }

    render() {

        return (
            <div>
                <h1>题目</h1>

                <div className="options">
                    题目列表
                </div>
            </div>
        )
    }

    componentWillMount() {
        this.props.getTim();
    }

    componentWillReceiveProps() {
        console.log("componentWillReceiveProps接受到到数据：")
        console.log(this.props)
    }


}



//将state映射到props函数
function mapStateToProps(state) {
    return {
        value: state.num
    }
}

const addAction = {
    type: 'add'
}
//将修改state数据的方法，映射到props,默认会传入store里的dispach方法
function mapDispatchToProps(dispatch) {
    return {

        getTim: async () => {
            let list = await fns.TimList();

            dispatch({ type: "setTimu", content: list })

            console.log("mapDispatchToProps设置的数据：" + list)
        }
    }
}

//将上面的这2个方法，将数据仓库的state和修改state的方法映射到组件上，形成新的组件。
const Dati = connect(
    mapStateToProps,
    mapDispatchToProps
)(DatiCounter)


export default Dati
